function SL({ data, color = "#4FFFB0", width = 140, height = 44, dates, unit = "" }) {
  const [hover, setHover] = useState(null);
  const ref = useRef(null);
  if (!data || data.length < 2) return null;
  const mn = Math.min(...data) * 0.93, mx = Math.max(...data) * 1.07, rg = mx - mn || 1;
  const pts = data.map((v, i) => ({ x: (i / (data.length - 1)) * width, y: height - ((v - mn) / rg) * height, v, i }));
  const uid = `sp-${Math.random().toString(36).slice(2, 8)}`;

  function handleInteraction(e) {
    if (!ref.current) return;
    const rect = ref.current.getBoundingClientRect();
    const clientX = e.touches ? e.touches[0].clientX : e.clientX;
    const relX = (clientX - rect.left) / rect.width * width;
    let closest = 0;
    let minDist = Infinity;
    pts.forEach((p, i) => {
      const d = Math.abs(p.x - relX);
      if (d < minDist) { minDist = d; closest = i; }
    });
    setHover(closest);
  }

  return (
    <div style={{ position: "relative" }}>
      <svg ref={ref} width={width} height={height} viewBox={`0 0 ${width} ${height}`} style={{ display: "block", width: "100%", touchAction: "none" }}
        onMouseMove={handleInteraction} onTouchMove={handleInteraction}
        onMouseLeave={() => setHover(null)} onTouchEnd={() => setHover(null)}>
        <defs><linearGradient id={uid} x1="0" y1="0" x2="0" y2="1"><stop offset="0%" stopColor={color} stopOpacity="0.3" /><stop offset="100%" stopColor={color} stopOpacity="0" /></linearGradient></defs>
        <path d={`M${pts.map(p => `${p.x},${p.y}`).join(" L")} L${width},${height} L0,${height} Z`} fill={`url(#${uid})`} />
        <path d={`M${pts.map(p => `${p.x},${p.y}`).join(" L")}`} fill="none" stroke={color} strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round" />
        {hover != null ? (
          <circle cx={pts[hover].x} cy={pts[hover].y} r="5" fill={color} stroke="#0A0C10" strokeWidth="2" />
        ) : (
          <circle cx={pts[pts.length-1].x} cy={pts[pts.length-1].y} r="4" fill={color} stroke="#0A0C10" strokeWidth="2" />
        )}
        {hover != null && <line x1={pts[hover].x} y1="0" x2={pts[hover].x} y2={height} stroke={color} strokeOpacity="0.2" strokeWidth="1" />}
      </svg>
      {hover != null && (
        <div style={{ position: "absolute", top: -32, left: Math.min(Math.max(pts[hover].x / width * 100, 15), 85) + "%", transform: "translateX(-50%)", background: "#1E2128", border: "1px solid " + color + "30", borderRadius: 8, padding: "4px 8px", whiteSpace: "nowrap", zIndex: 10, pointerEvents: "none" }}>
          <span style={{ fontSize: 11, fontWeight: 700, color: color }}>{data[hover]}{unit}</span>
          {dates && dates[hover] && <span style={{ fontSize: 9, color: "#6B7280", marginLeft: 6 }}>{dates[hover]}</span>}
        </div>
      )}
    </div>
  );
}


