function HopProfile({ athlete, norms, groupInfo, compareGroup, setCompareGroup, onBack }) {
  const hop = athlete.hop;
  const hopGroupAthletes = HOP_ATHLETES.filter(a => a.group === athlete.group);
  const hopGroupLabel = (GROUPS[athlete.group] || {}).label || "All";
  const hopGroupCount = hopGroupAthletes.length;
  const hopRsiRank = hopGroupAthletes.filter(a => a.best.rsi > athlete.best.rsi).length + 1;
  const hopFtRank = hopGroupAthletes.filter(a => a.best.ft > athlete.best.ft).length + 1;
  const hopCtRank = hopGroupAthletes.filter(a => a.best.ct < athlete.best.ct).length + 1;
  const gi = groupInfo;
  const eG = compareGroup || athlete.group;
  const n = (HOP_NORMS[eG] || HOP_NORMS.all);
  const egi = GROUPS[eG];

  const rm = [
    { sl: "RSI", value: hop.rsi, norms: n.rsi },
    { sl: "FT", value: hop.ft, norms: n.ft, unit: " ms" },
    { sl: "CT", value: hop.ct, norms: n.ct, invert: true, unit: " ms" },
  ];

  return (<>
    <div style={{ marginBottom: 16, padding: 16, background: `linear-gradient(135deg, ${egi.color}0A 0%, rgba(0,180,216,0.04) 100%)`, border: `1px solid ${egi.color}20`, borderRadius: 16 }}>
      {onBack && <button onClick={onBack} style={{ border: "none", background: "none", color: "#6B7280", fontSize: 13, cursor: "pointer", padding: "4px 0", marginBottom: 12 }}>{"\u2190"} Change Athlete</button>}
      <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 14 }}>
        <div style={{ width: 50, height: 50, borderRadius: 14, background: `linear-gradient(135deg, ${egi.color} 0%, #00B4D8 100%)`, display: "flex", alignItems: "center", justifyContent: "center", fontSize: 17, fontWeight: 700, color: "#0A0C10" }}>{athlete.initials}</div>
        <div>
          <div style={{ fontSize: 18, fontWeight: 700, color: "#fff" }}>{athlete.name}</div>
          <div style={{ fontSize: 12, color: "#8A8F98" }}><span style={{ color: egi.color, fontWeight: 600 }}>{egi.label}</span> {"\u00b7"} {athlete.bw} lbs {"\u00b7"} {athlete.testCount} sessions {"\u00b7"} Updated {LAST_UPDATED}</div>
        </div>
      </div>
      <div style={{ display: "flex", gap: 6 }}>
        {[{ v: hop.rsi, l: "RSI", c: "#FF6B6B" }, { v: hop.ct + "ms", l: "Contact Time", c: "#4FFFB0" }, { v: hop.ft + "ms", l: "Flight Time", c: "#60A5FA" }].map(s => (
          <div key={s.l} style={{ flex: 1, background: "rgba(0,0,0,0.3)", borderRadius: 12, padding: "10px 0", textAlign: "center" }}>
            <div style={{ fontSize: 18, fontWeight: 700, color: s.c }}>{s.v}</div>
            <div style={{ fontSize: 8, color: "#8A8F98", textTransform: "uppercase", letterSpacing: 0.6, marginTop: 2 }}>{s.l}</div>
          </div>))}
      </div>
    </div>
    <div style={{ marginBottom: 16 }}>
      <div style={{ fontSize: 9, color: "#6B7280", textTransform: "uppercase", letterSpacing: 1, marginBottom: 6 }}>Compare against</div>
      <div style={{ display: "flex", gap: 5, flexWrap: "wrap" }}>
        {Object.entries(GROUPS).map(([key, g]) => { if (!HOP_NORMS[key]) return null; const isA = eG === key; return (<button key={key} onClick={() => setCompareGroup(key)} style={{ padding: "7px 10px", border: `1px solid ${isA ? g.color : "rgba(255,255,255,0.06)"}`, borderRadius: 8, cursor: "pointer", fontSize: 10, fontWeight: 600, background: isA ? `${g.color}15` : "rgba(255,255,255,0.02)", color: isA ? g.color : "#6B7280" }}>{g.shortLabel}</button>); })}
      </div>
    </div>
    <div style={{ background: "rgba(255,255,255,0.03)", border: "1px solid rgba(255,255,255,0.06)", borderRadius: 16, padding: "18px 12px 12px", marginBottom: 16, textAlign: "center" }}>
      <div style={{ fontSize: 10, color: "#8A8F98", textTransform: "uppercase", letterSpacing: 1.2, marginBottom: 2 }}>Hop Profile vs. <span style={{ color: egi.color, fontWeight: 700 }}>{egi.label}</span></div>
      <div style={{ display: "flex", justifyContent: "center" }}><PR metrics={rm} gc={egi.color} /></div>
    </div>
    <MC metricKey="rsi" value={hop.rsi} unit="" norms={n.rsi} sparkData={athlete.rsiHistory} sparkColor="#FF6B6B" bestValue={athlete.best.rsi} sparkDates={HOP_SESSION_DATES[athlete.name]} rank={hopRsiRank} rankTotal={hopGroupCount} rankLabel={hopGroupLabel} overrideTitle="Hop RSI" overrideIcon="" overrideDesc="Flight time divided by contact time. The key measure of reactive ability during repeated hops." />
    <MC metricKey="ft" value={hop.ft} unit="ms" norms={n.ft} sparkData={athlete.ftHistory} sparkColor="#60A5FA" bestValue={athlete.best.ft} sparkDates={HOP_SESSION_DATES[athlete.name]} sparkUnit="ms" rank={hopFtRank} rankTotal={hopGroupCount} rankLabel={hopGroupLabel} overrideTitle="Flight Time" overrideIcon="" overrideDesc="How long the athlete is airborne between hops. Longer flight time with short ground contact shows elite reactive power." />
    <MC metricKey="ct" value={hop.ct} unit="ms" norms={n.ct} sparkData={athlete.ctHistory} sparkColor="#4FFFB0" bestValue={athlete.best.ct} sparkDates={HOP_SESSION_DATES[athlete.name]} sparkUnit="ms" rank={hopCtRank} rankTotal={hopGroupCount} rankLabel={hopGroupLabel} invert={true} overrideTitle="Contact Time" overrideIcon="" overrideDesc="Ground contact duration between hops. Shorter contact times with maintained height show better stiffness and reactive ability." />
  </>);
}

function HopStandingsTab({ filterGroup, onSelect }) {
  const [metric, setMetric] = useState("rsi");
  const [period, setPeriod] = useState("all");
  const periods = { all: "All-Time", tm: "This Month", lm: "Last Month", tw: "This Week" };
  const cfgs = {
    rsi: { label: "RSI", unit: "", key: "rsi", color: "#FF6B6B", desc: "Flight time divided by contact time. The key measure of how efficiently the athlete uses ground contact to produce explosive hops." },
    ct: { label: "Contact Time", unit: "ms", key: "ct", color: "#4FFFB0", invert: true, desc: "How long the foot stays on the ground between hops. Shorter contact times with maintained height show better reactive ability." },
    ft: { label: "Flight Time", unit: "ms", key: "ft", color: "#60A5FA", desc: "How long the athlete is airborne between hops. Longer flight time with short ground contact shows elite reactive power." },
  };
  const cfg = cfgs[metric];
  const sorted = useMemo(() => {
    let list = filterGroup === "all" ? [...HOP_ATHLETES] : HOP_ATHLETES.filter(a => a.group === filterGroup);
    if (period !== "all") list = list.filter(a => a.pb[period] != null);
    list.sort((a, b) => {
      const aVal = period === "all" ? a.best[cfg.key] : (a.pb[period] ? a.pb[period][cfg.key] : 0);
      const bVal = period === "all" ? b.best[cfg.key] : (b.pb[period] ? b.pb[period][cfg.key] : 0);
      return cfg.invert ? aVal - bVal : bVal - aVal;
    });
    return list;
  }, [metric, filterGroup, period]);

  function getVal(a) {
    const v = period === "all" ? a.best[cfg.key] : (a.pb[period] ? a.pb[period][cfg.key] : 0);
    return v;
  }

  return (
    <div>
      <div style={{ display: "flex", gap: 5, marginBottom: 16, flexWrap: "wrap" }}>
        {Object.entries(cfgs).map(([k, v]) => (
          <button key={k} onClick={() => setMetric(k)} style={{ padding: "7px 10px", border: `1px solid ${metric === k ? v.color : "rgba(255,255,255,0.06)"}`, borderRadius: 8, cursor: "pointer", fontSize: 10, fontWeight: 600, background: metric === k ? `${v.color}15` : "rgba(255,255,255,0.02)", color: metric === k ? v.color : "#6B7280" }}>{v.label}</button>
        ))}
      </div>
      <div style={{ fontSize: 11, color: "#8A8F98", lineHeight: 1.5, marginBottom: 14, padding: "10px 12px", background: "rgba(255,255,255,0.02)", borderRadius: 10, borderLeft: `3px solid ${cfg.color}` }}>{cfg.desc}</div>
      <div style={{ display: "flex", gap: 5, marginBottom: 16 }}>
        {Object.entries(periods).map(([k, v]) => (
          <button key={k} onClick={() => setPeriod(k)} style={{ padding: "7px 10px", border: `1px solid ${period === k ? "#C084FC" : "rgba(255,255,255,0.06)"}`, borderRadius: 8, cursor: "pointer", fontSize: 10, fontWeight: 600, background: period === k ? "rgba(192,132,252,0.12)" : "rgba(255,255,255,0.02)", color: period === k ? "#C084FC" : "#6B7280" }}>{v}</button>
        ))}
      </div>
      {sorted.length >= 3 && (
        <div style={{ display: "flex", gap: 8, marginBottom: 16, alignItems: "flex-end" }}>
          {[sorted[1], sorted[0], sorted[2]].map((a, idx) => {
            const rank = idx === 0 ? 2 : idx === 1 ? 1 : 3; const heights = [90, 110, 75]; const medals = {1: "\uD83E\uDD47", 2: "\uD83E\uDD48", 3: "\uD83E\uDD49"}; const ag = GROUPS[a.group];
            return (
              <div key={a.id} onClick={() => onSelect && onSelect(a.name)} style={{ flex: 1, textAlign: "center", cursor: onSelect ? "pointer" : "default" }}>
                <div style={{ fontSize: 22, marginBottom: 4 }}>{medals[rank]}</div>
                <div style={{ fontSize: 11, fontWeight: 700, color: "#fff", marginBottom: 2, whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis" }}>{a.name.split(" ")[0]}</div>
                <div style={{ fontSize: 9, color: "#6B7280", marginBottom: 6 }}>{a.name.split(" ").slice(1).join(" ")}</div>
                <div style={{ height: heights[idx], borderRadius: "12px 12px 0 0", background: `linear-gradient(180deg, ${cfg.color}30 0%, ${cfg.color}08 100%)`, border: `1px solid ${cfg.color}25`, borderBottom: "none", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center" }}>
                  <div style={{ fontSize: rank === 1 ? 22 : 18, fontWeight: 700, color: cfg.color }}>{getVal(a)}</div>
                  <div style={{ fontSize: 9, color: "#8A8F98" }}>{cfg.unit}</div>
                </div>
              </div>);
          })}
        </div>
      )}
      <div style={{ background: "rgba(255,255,255,0.02)", borderRadius: 14, overflow: "hidden" }}>
        {sorted.map((a, idx) => { const ag = GROUPS[a.group]; return (
          <div key={a.id} onClick={() => onSelect && onSelect(a.name)} style={{ display: "flex", alignItems: "center", gap: 10, padding: "10px 14px", cursor: onSelect ? "pointer" : "default", borderBottom: idx < sorted.length - 1 ? "1px solid rgba(255,255,255,0.04)" : "none" }}>
            <div style={{ width: 24, fontSize: 12, fontWeight: 700, textAlign: "center", color: idx < 3 ? cfg.color : "#6B7280" }}>{idx + 1}</div>
            <div style={{ width: 28, height: 28, borderRadius: "50%", flexShrink: 0, background: `linear-gradient(135deg, ${ag.color}40, ${ag.color}15)`, display: "flex", alignItems: "center", justifyContent: "center", fontSize: 9, fontWeight: 700, color: ag.color }}>{a.initials}</div>
            <div style={{ flex: 1, minWidth: 0 }}>
              <div style={{ fontSize: 12, fontWeight: 600, color: "#E0E0E0", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis" }}>{a.name}</div>
              <div style={{ fontSize: 9, color: ag.color }}>{ag.label}</div>
            </div>
            <div style={{ fontSize: 14, fontWeight: 700, color: idx < 3 ? cfg.color : "#C0C4CC" }}>{getVal(a)}{cfg.unit}</div>
          </div>); })}
      </div>
    </div>
  );
}

function HopTrendingTab({ filterGroup, onSelect }) {
  const [metric, setMetric] = useState("rsi");
  const cfgs = { rsi: { label: "RSI", color: "#FF6B6B", ck: "rsi_change", fk: "rsi_first", lk: "rsi_last", unit: "" }, ft: { label: "Flight Time", color: "#60A5FA", ck: "ft_change", fk: "ft_first", lk: "ft_last", unit: "ms" }, ct: { label: "Contact Time", color: "#4FFFB0", ck: "ct_change", fk: "ct_first", lk: "ct_last", unit: "ms" } };
  const cfg = cfgs[metric];
  const sorted = useMemo(() => {
    let list = filterGroup === "all" ? [...HOP_TRENDS] : HOP_TRENDS.filter(t => t.group === filterGroup);
    if (metric === "ct") {
      list.sort((a, b) => a[cfg.ck] - b[cfg.ck]);
    } else {
      list.sort((a, b) => b[cfg.ck] - a[cfg.ck]);
    }
    return list;
  }, [metric, filterGroup]);
  const isCtMetric = metric === "ct";
  const movers = isCtMetric ? sorted.filter(t => t[cfg.ck] < 0) : sorted.filter(t => t[cfg.ck] > 0);
  const decliners = isCtMetric ? [...sorted.filter(t => t[cfg.ck] >= 0)] : [...sorted.filter(t => t[cfg.ck] <= 0)].reverse();

  function Row({ t, idx, isUp }) {
    const ag = GROUPS[t.group]; const parts = t.name.split(" ");
    const ini = (parts[0][0] + parts[parts.length - 1][0]).toUpperCase();
    const changeVal = isCtMetric ? (isUp ? t[cfg.ck] : "+" + Math.abs(t[cfg.ck])) : (isUp ? "+" + t[cfg.ck] : t[cfg.ck]);
    return (
      <div style={{ display: "flex", alignItems: "center", gap: 10, padding: "10px 14px", borderBottom: "1px solid rgba(255,255,255,0.04)" }}>
        {isUp && <div style={{ width: 24, fontSize: 12, fontWeight: 700, textAlign: "center", color: idx < 3 ? "#4FFFB0" : "#6B7280" }}>{idx + 1}</div>}
        <div onClick={() => onSelect && onSelect(t.name)} style={{ width: 28, height: 28, borderRadius: "50%", flexShrink: 0, background: `linear-gradient(135deg, ${ag.color}40, ${ag.color}15)`, display: "flex", alignItems: "center", justifyContent: "center", fontSize: 9, fontWeight: 700, color: ag.color }}>{ini}</div>
        <div style={{ flex: 1, minWidth: 0 }}>
          <div style={{ fontSize: 12, fontWeight: 600, color: "#E0E0E0", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis" }}>{t.name}</div>
          <div style={{ fontSize: 9, color: "#6B7280" }}>{t[cfg.fk]}{cfg.unit} {"\u2192"} {t[cfg.lk]}{cfg.unit} {"\u00b7"} {t.sessions} sessions</div>
        </div>
        <div style={{ fontSize: 13, fontWeight: 700, color: isUp ? "#4FFFB0" : "#FF6B6B" }}>{changeVal}%</div>
      </div>);
  }

  return (
    <div>
      <div style={{ fontSize: 11, color: "#8A8F98", marginBottom: 12, lineHeight: 1.5 }}>Comparing each athlete's first 3 sessions vs. last 3 sessions.</div>
      <div style={{ display: "flex", gap: 5, marginBottom: 16 }}>
        {Object.entries(cfgs).map(([k, v]) => (
          <button key={k} onClick={() => setMetric(k)} style={{ padding: "7px 12px", border: `1px solid ${metric === k ? v.color : "rgba(255,255,255,0.06)"}`, borderRadius: 8, cursor: "pointer", fontSize: 10, fontWeight: 600, background: metric === k ? `${v.color}15` : "rgba(255,255,255,0.02)", color: metric === k ? v.color : "#6B7280" }}>{v.label}</button>
        ))}
      </div>
      <div style={{ display: "flex", alignItems: "center", gap: 8, marginBottom: 12 }}>
        <div style={{ width: 3, height: 16, background: "#4FFFB0", borderRadius: 2 }} />
        <h3 style={{ margin: 0, fontSize: 13, fontWeight: 700, color: "#E0E0E0" }}>{"\uD83D\uDCC8"} Trending Up</h3>
      </div>
      <div style={{ background: "rgba(255,255,255,0.02)", borderRadius: 14, overflow: "hidden", marginBottom: 20 }}>
        {movers.slice(0, 10).map((t, idx) => <Row key={t.name} t={t} idx={idx} isUp={true} />)}
      </div>
      {decliners.length > 0 && (<>
        <div style={{ display: "flex", alignItems: "center", gap: 8, marginBottom: 12 }}>
          <div style={{ width: 3, height: 16, background: "#FF8C42", borderRadius: 2 }} />
          <h3 style={{ margin: 0, fontSize: 13, fontWeight: 700, color: "#E0E0E0" }}>{"\uD83D\uDCC9"} Needs Attention</h3>
        </div>
        <div style={{ background: "rgba(255,255,255,0.02)", borderRadius: 14, overflow: "hidden" }}>
          {decliners.slice(0, 10).map((t, idx) => <Row key={t.name} t={t} idx={idx} isUp={false} />)}
        </div>
      </>)}
    </div>
  );
}


