function AsymIT({ title, desc, children }) {
  const [o, setO] = useState(false);
  const ref = useRef(null);
  useEffect(() => {
    if (!o) return;
    function h(e) { if (ref.current && !ref.current.contains(e.target)) setO(false); }
    document.addEventListener("mousedown", h); document.addEventListener("touchstart", h);
    return () => { document.removeEventListener("mousedown", h); document.removeEventListener("touchstart", h); };
  }, [o]);
  return (
    <span ref={ref} style={{ position: "relative", display: "inline-flex", alignItems: "center", gap: 4 }}>
      {children}
      <span onClick={(e) => { e.stopPropagation(); setO(!o); }}
        onMouseEnter={() => { if (window.matchMedia("(hover:hover)").matches) setO(true); }}
        onMouseLeave={() => { if (window.matchMedia("(hover:hover)").matches) setO(false); }}
        style={{ display: "inline-flex", alignItems: "center", justifyContent: "center", width: 15, height: 15, borderRadius: "50%", fontSize: 8, fontWeight: 700, background: o ? "rgba(79,255,176,0.2)" : "rgba(255,255,255,0.08)", color: o ? "#4FFFB0" : "#6B7280", cursor: "pointer", flexShrink: 0 }}>?</span>
      {o && (
        <div style={{ position: "absolute", bottom: "calc(100% + 8px)", left: "50%", transform: "translateX(-50%)", width: 270, background: "#1E2128", border: "1px solid rgba(79,255,176,0.15)", borderRadius: 12, padding: "14px 16px", zIndex: 200, boxShadow: "0 12px 40px rgba(0,0,0,0.6)" }}>
          <div style={{ fontSize: 13, fontWeight: 700, color: "#4FFFB0", marginBottom: 6 }}>{title}</div>
          <div style={{ fontSize: 11, color: "#B0B4BC", lineHeight: 1.6 }}>{desc}</div>
        </div>
      )}
    </span>
  );
}

function AsymmetrySection({ athleteName }) {
  const data = ASYMMETRY[athleteName];
  if (!data) return null;

  const metrics = [
    { label: "Concentric Impulse", value: data.conImpulse, side: data.conSide, desc: "Measures the balance of total push-off force between legs during the jump. A significant imbalance here means one leg is doing more of the work to get off the ground." },
    { label: "Ecc Braking Impulse", value: data.eccBraking, side: data.eccSide, desc: "Measures how evenly each leg absorbs force during the downward phase of the jump. Imbalances here can indicate compensation patterns and increased injury risk to the overloaded side." },
    { label: "Conc Peak Force", value: data.concPeakForce, side: data.cpfSide, desc: "Compares the maximum force each leg produces at the most explosive moment of the jump. Large differences may point to strength deficits or guarding behavior from a prior injury." },
  ];

  function getColor(val) {
    if (val <= 10) return "#4FFFB0";
    if (val <= 15) return "#FFB020";
    return "#FF6B6B";
  }

  function getLabel(val) {
    if (val <= 10) return "Symmetrical";
    if (val <= 15) return "Monitor";
    return "Flag";
  }

  const maxAsym = Math.max(data.conImpulse, data.eccBraking, data.concPeakForce);

  return (
    <div style={{ background: "rgba(255,255,255,0.03)", border: `1px solid ${maxAsym > 15 ? "rgba(255,107,107,0.2)" : maxAsym > 10 ? "rgba(255,176,32,0.15)" : "rgba(255,255,255,0.06)"}`, borderRadius: 16, padding: "18px 16px", marginBottom: 12 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 14 }}>
        <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
          <span style={{ fontSize: 18 }}>{"\u2696\uFE0F"}</span>
          <span style={{ fontSize: 14, fontWeight: 600, color: "#E0E0E0" }}>Left / Right Asymmetry</span>
        </div>
        <div style={{ fontSize: 9, fontWeight: 700, color: getColor(maxAsym), background: getColor(maxAsym) + "15", padding: "3px 10px", borderRadius: 10 }}>{getLabel(maxAsym)}</div>
      </div>

      <div style={{ fontSize: 10, color: "#8A8F98", lineHeight: 1.5, marginBottom: 14, padding: "8px 10px", background: "rgba(255,255,255,0.02)", borderRadius: 8 }}>
        Asymmetry under 10% is considered normal. Between 10-15% should be monitored. Above 15% may increase injury risk and should be addressed in programming.
      </div>

      <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: 16, marginBottom: 16, padding: "10px 0" }}>
        <div style={{ textAlign: "center" }}>
          <div style={{ fontSize: 10, color: "#6B7280", marginBottom: 4 }}>LEFT</div>
          <div style={{ fontSize: 18, fontWeight: 700, color: data.domSide === "L" ? "#60A5FA" : "#6B7280" }}>{data.lImpulse}<span style={{ fontSize: 10, fontWeight: 400, color: "#6B7280" }}> N{"\u00b7"}s</span></div>
          {data.domSide === "L" && <div style={{ fontSize: 8, color: "#60A5FA", fontWeight: 700, marginTop: 2 }}>DOMINANT</div>}
        </div>
        <div style={{ width: 1, height: 36, background: "rgba(255,255,255,0.1)" }} />
        <div style={{ textAlign: "center" }}>
          <div style={{ fontSize: 10, color: "#6B7280", marginBottom: 4 }}>RIGHT</div>
          <div style={{ fontSize: 18, fontWeight: 700, color: data.domSide === "R" ? "#60A5FA" : "#6B7280" }}>{data.rImpulse}<span style={{ fontSize: 10, fontWeight: 400, color: "#6B7280" }}> N{"\u00b7"}s</span></div>
          {data.domSide === "R" && <div style={{ fontSize: 8, color: "#60A5FA", fontWeight: 700, marginTop: 2 }}>DOMINANT</div>}
        </div>
      </div>

      {metrics.map((m, i) => {
        const c = getColor(m.value);
        const pos = 50 + (m.side === "R" ? 1 : -1) * Math.min(m.value, 30) / 30 * 50;
        return (
          <div key={i} style={{ marginBottom: i < metrics.length - 1 ? 14 : 0 }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 4 }}>
              <AsymIT title={m.label} desc={m.desc}><span style={{ fontSize: 11, fontWeight: 600, color: "#C0C4CC" }}>{m.label}</span></AsymIT>
              <span style={{ fontSize: 12, fontWeight: 700, color: c }}>{m.value}% <span style={{ fontSize: 9, fontWeight: 600, color: "#8A8F98" }}>{m.side}</span></span>
            </div>
            <div style={{ position: "relative", height: 10, background: "rgba(255,255,255,0.04)", borderRadius: 5 }}>
              <div style={{ position: "absolute", left: 0, top: 0, width: "50%", height: "100%", background: "rgba(96,165,250,0.06)", borderRadius: "5px 0 0 5px" }} />
              <div style={{ position: "absolute", left: "50%", top: 0, width: "50%", height: "100%", background: "rgba(255,107,107,0.06)", borderRadius: "0 5px 5px 0" }} />
              <div style={{ position: "absolute", left: "50%", top: -2, width: 2, height: 14, background: "rgba(255,255,255,0.2)", transform: "translateX(-50%)" }} />
              <div style={{ position: "absolute", left: pos + "%", top: -3, width: 16, height: 16, borderRadius: "50%", background: c, border: "2px solid #0A0C10", transform: "translateX(-50%)", boxShadow: "0 0 8px " + c + "50", transition: "left 0.6s ease" }} />
            </div>
            <div style={{ display: "flex", justifyContent: "space-between", marginTop: 3 }}>
              <span style={{ fontSize: 8, color: "#60A5FA" }}>L dominant</span>
              <span style={{ fontSize: 8, color: "#6B7280" }}>balanced</span>
              <span style={{ fontSize: 8, color: "#FF6B6B" }}>R dominant</span>
            </div>
          </div>
        );
      })}
    </div>
  );
}




